import React from 'react';
import { visualize, story, snap } from '../src';
import styles from './stories.scss';

const THUMBS_UP_SRC =
  'data:image/png;base64,';
const THUMBS_DOWN_SRC =
  'data:image/png;base64,';

interface ExampleComponentProps {
  onDone?(): void;
}

interface ExampleComponentState {
  isReady: boolean;
  counter: number;
}

class ExampleComponent extends React.Component<
  ExampleComponentProps,
  ExampleComponentState
> {
  private _timeoutId: NodeJS.Timeout;

  state = {
    isReady: typeof this.props.onDone !== 'function',
    counter: 0,
  };

  componentDidMount(): void {
    const { isReady } = this.state;

    if (!isReady) {
      this._timeoutId = setInterval(() => {
        this.setState({ counter: this.state.counter + 1 });
      }, 1000);
    }
  }

  componentDidUpdate(): void {
    const { counter, isReady } = this.state;
    const { onDone } = this.props;

    if (!isReady && counter === 5) {
      clearInterval(this._timeoutId);
      this.setState({ isReady: true }, onDone);
    }
  }

  componentWillUnmount(): void {
    clearInterval(this._timeoutId);
  }

  render() {
    const { isReady, counter } = this.state;

    return (
      <div style={{ fontFamily: 'Helvetica, Arial, sans-serif' }}>
        <h3>Story is {isReady ? 'Ready' : 'Waiting'}</h3>
        <div className={`${styles.box} ${isReady ? styles.ready : ''}`}>
          <img src={isReady ? THUMBS_UP_SRC : THUMBS_DOWN_SRC} alt="Thumbs" />
        </div>
        {!isReady ? <p>Ready in {5 - counter}s</p> : null}
      </div>
    );
  }
}

visualize('storybook-snapper', () => {
  story('Basic', () => {
    snap('sync example', () => <ExampleComponent />);
    snap('async example', done => <ExampleComponent onDone={done} />);

    snap(
      'run before',
      <div style={{ fontFamily: 'Helvetica, Arial, sans-serif' }}>
        <h3>Story is Waiting</h3>
        <div className={styles.box}>
          <img src={THUMBS_DOWN_SRC} alt="Thumbs" />
        </div>
        <p>Story will appear to not be ready without the <pre style={{display: 'inline'}}>runBefore</pre> script</p>
      </div>,
      async ({ rootEl }) => {
        const img = rootEl.querySelector('img') as HTMLImageElement;
        const header = rootEl.querySelector('h3') as HTMLHeadingElement;
        const imageWrapper = rootEl.querySelector(`.${styles.box}`);

        header.innerText = 'Story is Ready';
        imageWrapper.classList.add(styles.ready);

        return new Promise(res => {
          img.addEventListener('load', (e: UIEvent) => {
            if ((e.target as HTMLImageElement).src === THUMBS_UP_SRC) {
              res();
            }
          });

          img.src = THUMBS_UP_SRC;
        });
      },
    );
  });

  snap('sync example - without story', () => <ExampleComponent />);
});
